mapper.select:
SELECT a.click_date,IFNULL(b.amt,0) AS COUNT FROM ( SELECT CURDATE() AS click_date UNION ALL SELECT DATE_SUB(CURDATE(), INTERVAL 1 DAY) AS click_date UNION ALL SELECT DATE_SUB(CURDATE(), INTERVAL 2 DAY) AS click_date UNION ALL SELECT DATE_SUB(CURDATE(), INTERVAL 3 DAY) AS click_date UNION ALL SELECT DATE_SUB(CURDATE(), INTERVAL 4 DAY) AS click_date UNION ALL SELECT DATE_SUB(CURDATE(), INTERVAL 5 DAY) AS click_date UNION ALL SELECT DATE_SUB(CURDATE(), INTERVAL 6 DAY) AS click_date ) a LEFT JOIN ( SELECT s_name,DATE_FORMAT(s_createTime,'%Y-%m-%d') AS dt,count(1) as amt FROM student
and u_id=#{u_id}
and s_createUser=#{s_createUser}
GROUP BY dt ) b ON a.click_date = b.dt ORDER BY a.click_date ASC;
