mapper.select:
select 


			and u_id=#{u_id}


			and u_name=#{u_name}


			and u_pwd=#{u_pwd}


			and u_isLockout=#{u_isLockout}

select 


			and u_name like "%"#{u_name}"%"


			and u_isLockout=#{u_isLockout}


			and u_lastLoginTime >= #{beginlastLoginTime}


			and u_lastLoginTime <= #{endlastLoginTime}


			and u_createTime >= #{beginCreateTime}


			and u_createTime <= #{endCreateTime}


			and u_protectMtel =#{u_protectMtel}


			and u_protectEmail =#{u_protectEmail}



			limit #{page},#{rows}

select count(u_id) from users 


			and u_name like "%"#{u_name}"%"


			and u_isLockout=#{u_isLockout}


			and u_lastLoginTime >= #{beginlastLoginTime}


			and u_lastLoginTime <= #{endlastLoginTime}


			and u_createTime >= #{beginCreateTime}


			and u_createTime <= #{endCreateTime}


			and u_protectMtel =#{u_protectMtel}


			and u_protectEmail =#{u_protectEmail}

SELECT users.u_id,users.u_name,users.u_state FROM users INNER JOIN userroles ON users.u_id = userroles.u_id INNER JOIN roles ON userroles.r_id = roles.r_id
and u_name like "%"#{u_name}"%"
and u_state = #{u_state}
and roles.r_id=9
limit #{page},#{rows}
